operation.add:
        
		
		  public function template_mobile_theme_style() {
		  
		      $templates_mobile_styles = basename($this->request->get['template_mobile']);
			  $config_mobile_theme_style=$this->request->get['config_mobile_theme_style'];
			  $templates_mobile_style = array('Customize','Default');
			  $templates_mobile_style_names = '';
			  
			  $config_template_mobile = $this->config->get('config_template_mobile');
			  
			  $template_mobile = $this->request->get['template_mobile'];
			  if($template_mobile != 'opmex'){
			  	echo "<script>";
				echo "document.getElementById('mobile-theme-roller').style.display='none';";
				echo "document.getElementById('opmex_feature_product_view').style.display='none';";
				echo "document.getElementById('opmex_feature_custom_style').style.display='none';";
				echo "</script>";
			  }else{
			  	echo "<script>";
				echo "document.getElementById('opmex_feature_product_view').style.display='block';";
				echo "document.getElementById('opmex_feature_custom_style').style.display='block';";
				echo "</script>";
			  }
			  
			 
			foreach ($templates_mobile_style as $templates_mobile_style_name) {
				 if (file_exists(DIR_IMAGE . 'templates_mobile/'.$templates_mobile_styles.'/' . $templates_mobile_style_name.'_mobile_theme.png')) {
					$image = HTTP_CATALOG . 'image/' . 'templates_mobile/'.$templates_mobile_styles.'/' . $templates_mobile_style_name .'_mobile_theme.png';
				  } else {
					$image = HTTP_CATALOG . 'image/' . 'no_image.jpg';
				  }
				  if($config_mobile_theme_style == $templates_mobile_style_name) {
					$checked='checked="checked"';
				  }else{
					$checked='';
				  }
				  $templates_mobile_style_names.= '<img src="'.$image.'" height="20" width="20" title="'.$templates_mobile_style_name.'">&nbsp;<input type="radio" name="config_mobile_theme_style" id="config_mobile_theme_style" value="'. $templates_mobile_style_name.'" '.$checked.' onClick="check_mobile_custom_theme(this.value)"/>'.$templates_mobile_style_name.'&nbsp;';
			}
				$templates_mobile_style_names.='<input type="hidden" name="mobile_theme_change_flag" id="mobile_theme_change_flag" value="1"/>';
				$this->response->setOutput($templates_mobile_style_names);
				
				if($config_template_mobile != 'opmex') {
					echo '<script>';
					echo 'document.getElementById("config_mobile_theme_style").checked=false;';
					echo '</script>';
				}else if($config_template_mobile == 'opmex' && $config_mobile_theme_style=='Customize' && $template_mobile == 'opmex'){
					echo '<script>';
					echo "document.getElementById('mobile-theme-roller').style.display='block';";
					echo '</script>';
				} 
          }

        
          $_['entry_mobile_theme_style'] = 'Mobile Theme Style<br /><span class="help">Select Mobile theme style.</span>';
		  $_['entry_mobile_theme_product_view'] = 'Product View <br /><span class="help">Manage Category Product view for Mobile</span>';
		  $_['entry_mobile_theme_product_view_grid'] = 'Grid View';
		  $_['entry_mobile_theme_product_view_list'] = 'List View';

         $_['entry_mobile_theme_style'] = 'Mobile Theme Style<br /><span class="help">Select Mobile theme style.</span>';
		  $_['entry_mobile_theme_product_view'] = 'Product View <br /><span class="help">Manage Category Product view for Mobile</span>';
		  $_['entry_mobile_theme_product_view_grid'] = 'Grid View';
		  $_['entry_mobile_theme_product_view_list'] = 'List View';
